mapper.insert:
INSERT INTO egovdb.COMTNWORDDICARYINFO
    ( WORD_ID
      , WORD_NM
      , ENG_NM
      , WORD_DC
      , SYNONM
      , FRST_REGIST_PNTTM
      , FRST_REGISTER_ID )
VALUES ( #{wordId}
      , #{wordNm}
      , #{engNm}
      , #{wordDc}
      , #{synonm}
      , sysdate()
      , #{frstRegisterId} )

mapper.update:
UPDATE egovdb.COMTNWORDDICARYINFO	SET
       WORD_NM=#{wordNm}
     , ENG_NM=#{engNm}
     , WORD_DC=#{wordDc}
     , SYNONM=#{synonm}
     , LAST_UPDT_PNTTM=sysdate()
     , LAST_UPDUSR_ID=#{lastUpdusrId}
WHERE  WORD_ID=#{wordId}
